const getRank = (role) => {
    const r = (role || '').toUpperCase();
    if(r === 'SDO') return 1;
    if(r === 'EE' || r === 'FIELD') return 2;
    if(r === 'SE' || r === 'REVIEWER') return 3;
    if(r === 'CE') return 4;
    return 0; 
};

const checkJurisdiction = (user, site) => {
    if (!user.jurisdiction || user.jurisdiction.toLowerCase() === 'all') return true;
    const allowedItems = user.jurisdiction.split(',').map(s => s.trim().toLowerCase());
    const siteName = (site.name || "").toLowerCase();
    const siteDistrict = (site.district || "").toLowerCase();
    return allowedItems.some(item => 
        (item.length > 2 && siteName.includes(item)) || 
        (item.length > 2 && siteDistrict.includes(item))
    );
};

