const MapController = ({ filteredSites, selectedSite }) => {
  const map = useMap();
  useEffect(() => { 
      if (selectedSite) {
          map.flyTo([selectedSite.lat, selectedSite.lng], 13, { duration: 1.5 });
      } else if (filteredSites.length > 0) {
          const bounds = L.latLngBounds(filteredSites.map(s => [s.lat, s.lng]));
          map.fitBounds(bounds, { padding: [50, 50] });
      } else {
          map.setView([11.1271, 78.6569], 7); 
      }
  }, [filteredSites, selectedSite, map]);
  return null;
};

